console.log('ğŸ”„ behaviors/editor.jsonã‚’èª­ã¿è¾¼ã¿ä¸­...');
fetch('/editor/behaviors/editor.json').then(r => { console.log('ğŸ“¡ Fetchå®Œäº†:', r.status); return r.json(); }).then(behaviorDef => {
    console.log('âœ… JSONè§£æå®Œäº†:', behaviorDef);
    window.jsoniaRuntime = new JsoniaRuntime();
    window.jsoniaRuntime.init(behaviorDef);
    console.log('âœ… JsoniaRuntimeåˆæœŸåŒ–å®Œäº† - å®Œå…¨JSONé§†å‹•ã‚¨ãƒ‡ã‚£ã‚¿ ğŸ‰');

    // ã‚¯ãƒ©ã‚¤ã‚¢ãƒ³ãƒˆå´ã§ã‚¢ã‚³ãƒ¼ãƒ‡ã‚£ã‚ªãƒ³ã® style.json ã‚’å‹•çš„ã«èª­ã¿è¾¼ã¿ã€head ã«æ³¨å…¥ã™ã‚‹
    fetch('/editor/components/accordion-with-behavior/style.json')
      .then(r => { if (!r.ok) throw new Error('Style fetch failed ' + r.status); return r.json(); })
      .then(styleObj => {
        function camelToKebab(s) { return s.replace(/[A-Z]/g, m => '-' + m.toLowerCase()); }
        let css = '';
        for (const sel in styleObj) {
          css += sel + ' {\n';
          const rules = styleObj[sel];
          for (const prop in rules) {
            const val = rules[prop];
            const propName = camelToKebab(prop);
            css += '  ' + propName + ': ' + val + ';\n';
          }
          css += '}\n';
        }
        const styleEl = document.createElement('style');
        styleEl.setAttribute('data-injected-by','jsonia-editor');
        styleEl.textContent = css;
        document.head.appendChild(styleEl);
        console.log('âœ… accordion style injected');

        // Fallback: attach native click handlers to headers to call the runtime action directly
        function attachAccordionFallback() {
          try {
            // If Jsonia runtime delegates events globally, skip attaching per-element fallbacks
            if (window.__jsoniaDelegationActive) {
              console.log('â­ï¸ delegation active, skipping accordion fallbacks');
              return;
            }
            document.querySelectorAll('[data-accordion-header]').forEach(h => {
              if (h.__accordionFallbackAttached) return;
              h.addEventListener('click', (e) => {
                try {
                  // Skip fallback when the runtime's delegated handler already processed this event
                  if (e && e.__jsoniaHandled) return;
                  if (window.jsoniaRuntime && typeof window.jsoniaRuntime.toggleAccordion === 'function') {
                    window.jsoniaRuntime.toggleAccordion({ event: e });
                  }
                } catch (err) {
                  console.error('âš ï¸ accordion fallback handler error', err);
                }
              });
              h.__accordionFallbackAttached = true;
            });
          } catch (err) {
            console.warn('âš ï¸ attachAccordionFallback failed', err);
          }
        }
        attachAccordionFallback();
        const mo = new MutationObserver(() => attachAccordionFallback());
        mo.observe(document.body, { childList: true, subtree: true });
      })
      .catch(err => { console.warn('âš ï¸ accordion style load failed', err); });

}).catch(err => console.error('âŒ èª­ã¿è¾¼ã¿ã‚¨ãƒ©ãƒ¼:', err));
